import './App.css';
import  Dashboard  from './modules/Dashboard';
import  Form  from './modules/Form';
import {Routes, Route, redirect } from 'react-router-dom'

const ProtectedRoutes = ({children }) => {
  console.log('first', first)
   const isLoggedIn = localStorage.getItem('user:token') !== null;
   if(!isLoggedIn)
    redirect('/user/sign_in')

   return children
}


function App() {
  return (
   <>
   <Routes>
   <Route path='/' element={
    <ProtectedRoutes>
     <Dashboard/>
    </ProtectedRoutes>
   }></Route>

    <Route path='/users/sign_in' element={<Form isSignPage={true}/>}></Route>
    <Route path='/users/sign_up' element={<Form isSignPage={false}/>}></Route>
   </Routes>
   </>
  );
}

export default App;
