import React, { useState } from "react";
import "./index.css";
import Input from "../../Components/input";
import Button from "../../Components/Button";
import { useNavigate } from "react-router-dom";


const Form = ({ isSignPage = false }) => {
  const [data, setData] = useState({
    ...(!isSignPage && {
      fullName: "",
    }), // Set an empty string by default
    email: "",
    password: "",
  });


  const options = {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json' // Set content type to JSON
    },
    body: JSON.stringify(jsonData)
  };

  const navigate = useNavigate()
  const handleSubmit = async (e) => {
  e.preventDefault();

  fetch(`http://localhost:3000/api/${isSignPage ?  'login' : 'register'}` ,options) 
  .then(response => {
    // Check if the request was successful
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    // Parse the response as JSON
    return response.json();
  })
  .then(data => {
    // Handle the JSON data
    console.log(data);
  })
  .catch(error => {
    // Handle any errors that occurred during the fetch
    console.error('Fetch error:', error);
  });
  }

  return (
    <>
    <div className="mnpg1">
      <div className="fm-body-1">
      <div className="fm-body-1_1">Welcome {isSignPage && "Back"} !</div>
      <div className="fm-body-1_2">
        {isSignPage ? "Sign in to get Explored" : "Sign up to get started"}
      </div>
      <form  className="frm-clss" onSubmit={(e) => {handleSubmit(e)}}>
        {!isSignPage && 
          <Input
            label="Full Name "
            name="fullName"
            placeholder="Enter your Full Name: "
            value={data.fullName}
            onChange={(e) => setData({ ...data, fullName: e.target.value })}
          />
        }
        <Input
          label="Email address "
          name="email"
          type="email"
          placeholder="Enter your Email address: "
          value={data.email}
          onChange={(e) => setData({ ...data, email: e.target.value })}
        />
        <Input
          label="password "
          type="password"
          name="password"
          placeholder="Enter your Password: Â  
 "
          value={data.password}
          onChange={(e) => setData({ ...data, password: e.target.value })}
        />
        <Button type="Submit" label={isSignPage ? "Sign in" : "Sign up" }  />
      </form>

      <div>
        {isSignPage ? "Didn't have an account ?" : "Already have an account ?"}
        <span className="sgn-in" onClick={()=>navigate(`/users/${isSignPage ? 'sign_up ' : 'sign_in'}`)}>{isSignPage ? "Sign up" : "Sign in "}</span>
      </div>
    </div>
    </div>

    </>

  );
};

export default Form;
